package com.cmc.finder.api.auth.signup.dto;import lombok.*;import org.springframework.web.bind.annotation.RequestPart;import javax.validation.constraints.Email;import javax.validation.constraints.NotBlank;public class EmailAuthDto {    @Getter @Setter    public static class Request {        @NotBlank(message = "이메일은 필수값 입니다.")        @Email        private String email;        @NotBlank(message = "인증코드는 필수값 입니다.")        private String code;    }    @Builder    @Getter @Setter    public static class Response {        private String message;        public static Response of() {            return Response.builder()                    .message("인증 완료되었습니다.")                    .build();        }    }}